/**
 * Author: dpEarth
 * 
 * Purpose: The purpose of Navbar is to have a functional navigation bar at the 
 * top of the page with animation done in hte navbar.css file. 
 * 
 */
import React, {useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
import { Button } from './Button';
import './Navbar.css'


function Navbar() {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if(window.innerWidth <= 960){
        setButton(false);
    }   
    else{
        setButton(true);
    }
};

    useEffect(() => {
        showButton();
    }, []);

  window.addEventListener('resize', showButton);

    return (
        <>
            <nav className="navbar">
                <div className="navbar-container">

                <div className="menu-icon" onClick={handleClick}>
                   <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
                </div>
                    <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                        <li className="nav-item">
                            <Link to='/home' className='nav-links' onClick={closeMobileMenu}>
                                Home
                            </Link>
                        </li>
                        <li className="nav-item">
                            <Link to='/projects' className='nav-links' onClick={closeMobileMenu}>
                                Projects
                            </Link>
                        </li>
                        <li className="nav-item">
                            <Link to='/developers' className='nav-links' onClick={closeMobileMenu}>
                                Team
                            </Link>
                        </li>
                    </ul>
                    {button && <Button buttonStyle='btn--outline'>Join Us </Button>}
                </div>
            </nav>
        </>
    )
}

export default Navbar
