import React from "react";
import { Link } from "react-router-dom";
import "../Section/Section.css";
import foto from "../../images/earth.gif";
import gpi from "../../images/gpi.png";
import asi from "../../images/asi.png";
import "./Projects.css";
import ReactPlayer from "react-player";

import videoProjects from "../../video/video_projects_compressed.mp4";
import { useEffect } from "react";

class ProjectCard extends React.Component {
  render() {
    return (
      <div className="project-cardContainer">
        <div className="project-cardContainer-flex">
          <div className="project-place-img">
            <img src={this.props.pro_img} className="project-img"></img>
          </div>
          <div className="project-info flexbox-container">
            <div className="project-row-info">
              <h4 className="project-font-h4 texts-left">Authors</h4>
              <ul>
                {/* This is not good coding! Please do for loops */}
                {/* also here we can have one author and write all of our names */}
                <p className="project-font-authors texts-left">
                  {this.props.project_author1}
                </p>
                <p className="project-font-authors texts-left">
                  {this.props.project_author2}
                </p>
                <p className="project-font-authors texts-left">
                  {this.props.project_author3}
                </p>
                <p className="project-font-authors texts-left">
                  {this.props.project_author4}
                </p>
                <p className="project-font-authors texts-left date-break italics">
                  {this.props.project_date}
                </p>
              </ul>
            </div>
            <div className="project-row-info">
              <h4 className="project-font-h4">Links</h4>
              <ul>
                <h3 className="project-font-links">
                  <a href={this.props.project_link_url}>
                    {this.props.project_link}
                  </a>
                </h3>
              </ul>
            </div>
            <div className="project-row-info">
              <h4 className="project-font-h4 texts-right">Made with</h4>
              <ul>
                <p className="project-font-madewith">
                  {this.props.project_madeWith}
                </p>
              </ul>
            </div>
          </div>
          <h4 className="project-font-h4 texts-left">About the code</h4>
          <ul>
            <h3 className="project-font-aboutcode-title texts-left">
              {this.props.project_name}
            </h3>
            <p className="project-font-aboutcode-desc texts-left">
              {this.props.project_description}
            </p>
          </ul>

          <br />

          <div id="sameLine">
            <h4 className="project-font-h4 texts-left">Compatible Devices:</h4>
            <p className="project-font-brows-respons-dep">
              {this.props.devices}
            </p>
          </div>

          <div id="sameLine">
            <h4 className="project-font-h4 texts-left">Responsive:</h4>
            <p className="project-font-brows-respons-dep">
              {this.props.responsive}
            </p>
          </div>

          <div id="sameLine">
            <h4 className="project-font-h4 texts-left">Dependencies:</h4>
            <p className="project-font-brows-respons-dep">
              {this.props.dependencies}
            </p>
          </div>
        </div>
      </div>
    );
  }
}

export default function Products() {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  return (
    <>
      <ReactPlayer
        className="videoTag"
        playing="true"
        loop="true"
        moot="true"
        height="100vh"
        width="100vw"
        config={{
          file: {
            attributes: {
              style: {
                width: "100%",
                height: "100vh",
                objectFit: "cover",
              },
            },
          },
        }}
        url={videoProjects}
      />
      <div className="proj-intro">
        <h2 className="proj-intro-title"> Our Projects </h2>
        <h6 className="proj-intro-links"> Video by cottonbro from Pexels</h6>
      </div>

      <ProjectCard
        pro_img={foto}
        project_author1="Revaz Bakuradze"
        project_author2="Aleksandr Molchagin"
        project_author3="Davit Xu"
        project_author4="Fatima Ortega"
        project_date="12 September 2021"
        project_link="dVox"
        project_link_url="https://gfycat.com/brilliantlastkite-backpack-kid-dance-from-snl-the-backpack-kid"
        project_madeWith="vue.js"
        project_name="Voice your thoughts with complete anonimity.."
        project_description="Like share! Please use this project subscribe. follow buy out product let's have some more text. u done so initially or after @Tomalak's prompting, we would have answered your question for you by now"
        devices="iOS & Android"
        responsive="Yes"
        dependencies="So many canoot count"
      />

      {/* First card that has information on dVox */}
      <ProjectCard
        pro_img={foto}
        project_author1="Aleksandr"
        project_author2="Revaz"
        project_author3="David"
        project_author4="Fatima"
        project_name="dVox"
        project_description="About the app dVox, Deserunt ad adipisicing esse tempor officia."
      ></ProjectCard>

      {/* Second card that has information on css */}
      <ProjectCard
        pro_img={foto}
        project_author1="Aleksandr"
        project_author2="Revaz"
        project_author3="David"
        project_author4="Fatima"
        project_name="Computer Science Society"
        project_description="About CSS, Deserunt ad adipisicing esse tempor officia."
      ></ProjectCard>
    </>
  );
}
