import React from 'react';
import '../../App.css';
import logo from '../../images/dp_logo_color.svg'
import ContactForm from '../ContactForm/ContactForm';
import { Link } from 'react-router-dom';
import Section from '../Section/Section';
import { Helmet } from "react-helmet";

import Anime from "an"





export default function Home() {

    // componentDidMount() {
    //     const script = document.createElement("script");
    //     script.src = "https://cdn.jsdelivr.net/npm/kute.js@2.1.2/dist/kute.min.js";
    //     script.async = true;
    //     script.onload = () => this.scriptLoaded();

    //     document.body.appendChild(script);
    // }

    return (
        <>
            <div className="background-container">


                {/* this is the first section with logo and description*/}
                <div className="home-sec" >

                    <div className="homeFlexContainer">
                        <div className="homeFlex">

                            <img src={logo} alt=" background" className="bg-Logo" />

                            <div className="description-pos">
                                <p>Our goal in Decentralized People project is to create applications which allow people to connect through the use of blockchain system. </p>
                            </div>
                        </div>
                    </div>
                    <div className="curve" />
                </div>

                {/* this is the second section with the project button and description */}
                <div className="home-secBlack">
                    <div className="homeFlexContainer">
                        <div className="homeFlex">


                            <div className="description-pos">

                                <p style={{ color: "white" }}>Take a look at our amazing projects that we have developed!</p>
                            </div>

                            <div className="homeButton">
                                {/* <div className="application">
                                    <Helmet>
                                        <script src="https://cdn.jsdelivr.net/npm/kute.js@2.1.2/dist/kute.min.js"></script>
                                    </Helmet>
                                </div> */}

                                <div className="blob">
                                    <svg
                                        id="visual"
                                        viewBox="0 0 900 600"
                                        width="900"
                                        height="600"
                                        xmlns="http://www.w3.org/2000/svg"
                                        xmlnsXlink="http://www.w3.org/1999/xlink"
                                        version="1.1">
                                        <g transform="translate(417.72215628315143 309.077765694506)">
                                            <path id="blob1" d="M150.1 -153.2C196.9 -103.2 239 -51.6 239.8 0.8C240.6 53.3 200.2 106.5 153.4 150.9C106.5 195.2 53.3 230.6 4.8 225.8C-43.6 220.9 -87.2 175.9 -121 131.5C-154.9 87.2 -178.9 43.6 -174.8 4.1C-170.7 -35.4 -138.4 -70.7 -104.5 -120.7C-70.7 -170.7 -35.4 -235.4 8.1 -243.5C51.6 -251.6 103.2 -203.2 150.1 -153.2" fill="#FFFFFF">
                                            </path>
                                        </g>
                                        <g transform="translate(411.1149713450477 289.2665038557717)">
                                            <path id="blob2" d="M137.2 -116.4C187.2 -87.2 243.6 -43.6 256.9 13.3C270.2 70.2 240.5 140.5 190.5 165.6C140.5 190.8 70.2 170.9 1.6 169.3C-66.9 167.6 -133.9 184.2 -163 159C-192.2 133.9 -183.6 66.9 -167.8 15.8C-152 -35.4 -129 -70.7 -99.9 -99.9C-70.7 -129 -35.4 -152 4.1 -156.1C43.6 -160.3 87.2 -145.5 137.2 -116.4" fill="#FFFFFF">
                                            </path>
                                        </g>
                                    </svg>

                                    {/* <div ref={el => (this.div = el)}>
                                        <div>const tween = KUTE.fromTo(
                                            '#blob1',
                                            {path: '#blob1'},
                                            {path: '#blob2'},
                                            {repeat: 999, duration: 30000, yoyo: true}
                                            )
                                            tween.start()
                                        </div> */}
                                    {/* </div> */}
                                </div>

                                <Link to='/projects'>

                                    Projects
                                </Link>

                            </div>
                        </div>
                    </div>
                    <div className="curve2" />
                </div>

                {/* this is the third section with the developer button and description */}
                <div className="home-sec">

                    <div className="homeFlexContainer">
                        <div className="homeFlex">
                            <div className="description-pos">
                                <p>Get to know our developers!</p>
                            </div>
                            <div className="homeButton">
                                <Link to='/developers'>
                                    Developers
                                </Link>
                            </div>
                        </div>
                    </div>
                    <div className="curve3" />
                </div>

                {/* this will be the form to join */}
                <div className="home-secBlack">
                    <Section
                        title="Please fill in the form we would love to know more about you!"
                        subtitle={ContactForm}
                        dark={true}
                        id="joinUs"
                    />
                </div>
                <div className="curve4" />
            </div>


        </>
    );

}
