import React from "react";
import { Link } from "react-router-dom";
import "../Section/Section.css";
import foto from "../../images/earth.gif";

import dvox from "../../images/webposter_dvox.png";
import css from "../../images/webposter_css.jpg";
import projectvbw from "../../images/projectVBW.png";


import "./Projects.css";
import ReactPlayer from "react-player";

import videoProjects from "../../video/video_projects_compressed.mp4";
import { useEffect } from "react";

import DVox from "./DVox";

class ProjectCard extends React.Component {
  render() {
    return (
      <div className="project-cardContainer">
        <div className="project-cardContainer-flex">
          <div className="project-place-img">
            <Link to={this.props.project_link}>
              <img src={this.props.pro_img} className="project-img"></img>
            </Link>
          </div>
          <div className="project-info flexbox-container">
            <div className="project-row-info-left">
              <h4 className="project-font-h4 texts-left">Authors</h4>
              <ul>
                {/* This is not good coding! Please do for loops */}
                {/* also here we can have one author and write all of our names */}
                <p className="project-font-authors texts-left">
                  {this.props.project_author1}
                </p>
                <p className="project-font-authors texts-left">
                  {this.props.project_author2}
                </p>
                <p className="project-font-authors texts-left">
                  {this.props.project_author3}
                </p>
                <p className="project-font-authors texts-left">
                  {this.props.project_author4}
                </p>
                <p className="project-font-authors texts-left">
                  {this.props.project_author5}
                </p>
                <p className="project-font-authors texts-left">
                  {this.props.project_author6}
                </p>
              </ul>
            </div>
            <div className="project-row-info-right">
              <h4 className="project-font-h4">Year created</h4>
              <p className="project-font-authors">
                {this.props.project_date}
              </p>
            </div>
            {/* <div className="project-row-info">
              <h4 className="project-font-h4">Links</h4>
              <ul>
                <h3 className="project-font-links">
                  <a href={this.props.project_link_url}>
                    {this.props.project_link}
                  </a>
                </h3>
              </ul>
            </div>
            <div className="project-row-info">
              <h4 className="project-font-h4 texts-right">Made with</h4>
              <ul>
                <p className="project-font-madewith">
                  {this.props.project_madeWith}
                </p>
              </ul>
            </div> */}
          </div>

          <h4 className="project-font-h4 texts-left">About the project</h4>
          <ul>
            <h3 className="project-font-aboutcode-title">
              {this.props.project_name}
            </h3>
            <p className="project-font-aboutcode-desc">
              {this.props.project_description}
            </p>
          </ul>

          <br />
          <div id="sameLine">
            <h4 className="project-font-h4 texts-left">Audience:</h4>
            <p className="project-font-brows-respons-dep">
              {this.props.audience}
            </p>
          </div>
        </div>
          {this.props.project_link == "https://vbw.dpearth.com" 
            ?
           <div className="projects-button-learn-more" >
              <a>
                <button className="btnLearn">Learn more</button>
                </a>
            </div>
            :
            <div className="projects-button-learn-more" >

            </div>
          }
          {this.props.project_link != "" && this.props.project_link != "https://vbw.dpearth.com" ?
              <div className="projects-button-learn-more" >
                <Link to={this.props.project_link}>
                  <button className="btnLearn">Learn more</button>
                </Link>
              </div>
            : 
              <div className="projects-button-learn-more" >
                <Link to={this.props.project_link}>
                </Link>
               </div>
            }
      </div>
    );
  }
}

export default function Products() {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  return (
    <>
      <ReactPlayer
        className="videoTag"
        playing="true"
        loop="true"
        moot="true"
        height="100vh"
        width="100vw"
        config={{
          file: {
            attributes: {
              style: {
                width: "100%",
                height: "100vh",
                objectFit: "cover",
              },
            },
          },
        }}
        url={videoProjects}
      />
      <div className="proj-intro">
        <h2 className="proj-intro-title"> Our Projects </h2>
        <h6 className="proj-intro-links"> Video by cottonbro from Pexels</h6>
      </div>

      <ProjectCard
        pro_img={projectvbw}
        project_link="https://vbw.dpearth.com"
        // project_link='https://www.youtube.com/'
        project_author1="Aleksandr Molchagin"
        project_author2="Lucas Rizzolo"
        project_author3="David Xu"
        project_author4="William Shaw"
        project_author5="Revaz Bakuradze"
        project_author6="Terry Nuechterlein"
        project_date="2021"
        project_madeWith=""
        project_name="projectVBW"
        project_description="The internet, in its entirety, contributes strongly to the degradation of the environment. The words 'The Internet' can refer to a variety of unique, interacting architecture, so it is important to distinguish which aspects of the internet are being discussed. This article seeks to formalize information regarding the energy use and related carbon emissions of three specific aspects of the internet: infrastructures for data storage and transmission, personal devices connecting to the internet, and cryptocurrencies. We determine these to be relevant in understanding a minimal environmental impact on the environment, how individuals contribute to it, and how societal obsessions contribute to it. Each of the three aspects of the internet will discuss these, respectively."
        devices=" iOS & Android"
        audience=" Kalamazoo College Students"
        dependencies="TBD"
      />

      {/* First card that has information on dVox */}
      <ProjectCard
        pro_img={dvox}
        project_link="DVox"
        // project_link='https://www.youtube.com/'
        project_author1="Aleksandr Molchagin"
        project_author2="Revaz Bakuradze"
        project_author3="David Xu"
        project_author4="Fatima Ortega"
        project_date="2021"
        project_link="DVox"
        project_madeWith=""
        project_name="dVox"
        project_description="A decentralized application built to help Kalamazoo College students share their thoughts and ideas anonymously."
        devices=" iOS & Android"
        audience=" Kalamazoo College Students"
        dependencies="TBD"
      />

      {/* Second card that has information on css */}
      <ProjectCard
        pro_img={css}
        project_author1="Aleksandr Molchagin"
        project_author2="Revaz Bakuradze"
        project_author3="David Xu"
        project_author4="Fatima Ortega"
        project_date="2021"
        project_link=""
        // project_madeWith="The students of K"
        project_name="Computer Science Society"
        project_description="Computer Science Society is a student organization made to create a supportive community for students interested in the Computer Science field, where each member can improve their skills related but not limited to the Computer Science field. "
        audience=" Kalamazoo College Students"
      ></ProjectCard>
    </>
  );
}
