import React from 'react';
import './Button.css';
import { Link as PageLink, animateScroll as scroll, Button as ScrollButton } from "react-scroll";
import { Link } from 'react-router-dom';
const STYLES = ['btn--primary', 'btn--outline'];

const SIZES = ['btn--medium', 'btn--large'];

export const Button = ({
    children, 
    type, 
    onClick, 
    buttonStyle, 
    buttonSize}) => {
        const checkButtonStyle = STYLES.includes(buttonStyle) 
        ? buttonStyle : 
        STYLES[0];

        const checkButtonSize = SIZES.includes(buttonSize) 
        ? buttonSize : 
        SIZES[0];

        return (

                    <Link to='/home'>
                        <ScrollButton
                        className={`btn ${checkButtonStyle} ${checkButtonSize}`}
                        onClick={setTimeout(scroll.scrollMore(2500), 5000)}
                        type={type}
                        activeClass="active"
                        to="joinUs"
                        spy={true}
                        smooth={true}
                        offset={-70}
                        duration={500}
                        >
                            {children}
                        </ScrollButton>
                    </Link>
        )
    };
