import React from 'react';
import './Developers.css';
import Aleksandr from '../../images/Aleksandr.jpeg';
import Fatima from '../../images/Fatima.jpg';

import videoDevelopers from '../../video/video_developers.mp4';
import SocialMediaButtons from 'react-social-media-buttons';


class Person extends React.Component {
    render(){
        return (
        <div className="developers-person-container">
            <div className="developers-person-sectionOne">
                <div className="card-teamDev"><img src={this.props.img} className="dev-placeimg"></img></div>
                <div className="developers-person-text-container">
                    <ul>
                        <h3 className="developers-person-name"> {this.props.name} </h3> 
                        <h4 className="developers-person-title"> {this.props.title} </h4> 
                        <h4 className="developers-person-title"> {this.props.subtitle} </h4> 
                        <h4 className="developers-person-email"> {this.props.email} </h4> 
                    </ul>
                    <div className="developers-person-social-media">
                        <SocialMediaButtons
                            links={[this.props.github, this.props.linkedin]}
                            buttonStyle={{margin: '0px 10px', backgroundColor: '#ffffff', borderRadius: '50%', border: '2px solid #000000'}}
                            iconStyle={{color: '#000000'}}
                            openNewTab={true}
                        />
                    </div>
                </div>
            </div>    
        </div>     
    )}
}



export default function Developers() {
    return (
        <>
            <video className='videoTag' autoPlay loop muted>
                <source src={videoDevelopers} type='video/mp4' />
            </video>
            <div className="dev-intro">
                <h2 className="dev-intro-title"> Our Developers </h2>
                <h6 className="dev-intro-links"> Video by Pavel Danilyuk from Pexels</h6>
            </div>
            <div className="dev-column">

                <Person 
                    name="Aleksandr Molchagin" 
                    title="Founder"
                    subtitle="CS & Business @ Kalamazoo College"
                    img={Aleksandr} 
                    email="contact@aleksandrmolchagin.com"
                    github="https://github.com/AleksandrMolchagin"
                    linkedin="https://www.linkedin.com/in/amolchagin/"
                ></Person>

                <Person 
                    name="Revaz Bakuradze" 
                    title="founder"
                    //img={} 
                    github="https://github.com/"
                    linkedin="https://www.linkedin.com/in//"
                    email="fatimarortega@gmail.com"
                 ></Person>   

            </div>
            <div className="dev-column">

                <Person 
                    name="David Xu" 
                    title="Founder"
                    subtitle="CS major & Math @ Kalamazoo College"
                    //img={} 
                    github="https://github.com/DaveXFin"
                    linkedin="https://www.linkedin.com/in/david-xu-976384207/"
                    email="david.xu@windowslive.com"
                 ></Person>  

                <Person 
                    name="Fatima Ortega" 
                    title="founder"
                    img={Fatima} 
                    github="https://github.com/DaveXFin"
                    linkedin="https://www.linkedin.com/in//"
                    email="fatimarortega@gmail.com"
                ></Person>            
            </div>
        </>
    )
}
