import React from 'react';
import { Link } from 'react-router-dom';
import '../Section/Section.css';
import foto from '../../images/earth.gif';
import gpi from '../../images/gpi.png';
import asi from '../../images/asi.png';
import './Projects.css';
import ReactPlayer from 'react-player'

import videoProjects from '../../video/video_projects_compressed.mp4';
import { useEffect } from 'react';


class Project_Card extends React.Component {
  render() {
    return (
      <div className="project-cardContainer">
        <div className="project-cardContainer-flex">
          <div className="project-place-img"><img src={this.props.pro_img} className="project-img"></img></div>
          <div className="project-info">
            <div className="project-row-info">
              <h4 className="project-font-h4">Authors</h4>
              <ul>
                <p> {this.props.project_author1} </p>
                <p> {this.props.project_author2} </p>
                <p> {this.props.project_author3} </p>
                <p> {this.props.project_author4} </p>
              </ul>
            </div>
            <div className="project-row-info">
              <h4 className="project-font-h4">Links</h4>
              <ul>
                <h3> {this.props.project_link} </h3>
              </ul>
            </div>
            <div className="project-row-info">
              <h4 className="project-font-h4">Made with</h4>
              <ul>
                <h3> {this.props.project_madeWith} </h3>
              </ul>
            </div>
          </div>
          <h4 className="project-font-h4">About the code</h4>
          <ul>
            <h3> {this.props.project_name} </h3>
            <p> {this.props.project_description} </p>
          </ul>

          <h4 className="project-font-h4">Compatible browsers:</h4>
          <h4 className="project-font-h4">Responsive:</h4>
          <h4 className="project-font-h4">Dependencies:</h4>
        </div>
      </div>

    )
  }
}

export default function Products() {

  useEffect(() => {
    window.scrollTo(0, 0)
  }, [])

  return (

    <>
    <ReactPlayer className='videoTag' 
      playing="true"
      loop="true"
      moot="true"
      height="100vh"
      width="100vw"
      config={{
          file: {
          attributes: {
              style: {
                width: "100%",
                height: "100vh",
                objectFit: "cover",
              },
          },
          },
        }}
        url={videoProjects}
      />
      <div className="proj-intro">
        <h2 className="proj-intro-title"> Our Projects </h2>
        <h6 className="proj-intro-links"> Video by cottonbro from Pexels</h6>
      </div>

      {/* First card that has information on dVox */}
      <Project_Card
        pro_img={foto}
        project_author1="Aleksandr"
        project_author2="Revaz"
        project_author3="David"
        project_author4="Fatima"
        project_name="dVox"
        project_description="About the app dVox, Deserunt ad adipisicing esse tempor officia."
      >
      </Project_Card>


      {/* Second card that has information on css */}
      <Project_Card
        pro_img={foto}
        project_author1="Aleksandr"
        project_author2="Revaz"
        project_author3="David"
        project_author4="Fatima"
        project_name="Computer Science Society"
        project_description="About CSS, Deserunt ad adipisicing esse tempor officia."
      >
      </Project_Card>
    </>

  )

}
