import './Button.css';
import { Link as PageLink, animateScroll as scroll, Button as ScrollButton } from "react-scroll";
import { Link } from 'react-router-dom';
const STYLES = ['btn--primary', 'btn--outline'];

const SIZES = ['btn--medium', 'btn--large'];

export const Button = ({
    children, 
    type, 
    onClick, 
    buttonStyle, 
    buttonSize}) => {
        const checkButtonStyle = STYLES.includes(buttonStyle) 
        ? buttonStyle : 
        STYLES[0];

        const checkButtonSize = SIZES.includes(buttonSize) 
        ? buttonSize : 
        SIZES[0];

        return (

            <div>
                <PageLink activeClass="active" to="joinUs" spy={true} smooth={true} offset={50} duration={500} delay={1000}>
                Test 2 (delay)
                </PageLink>
                <ScrollButton activeClass="active" className="btn" type="submit" value="Test 2" to="joinUs" spy={true} smooth={true} offset={50} duration={500} >
                Test 2
                </ScrollButton>
            </div>


        )
    };
